}{}\begin{equation*}{\rm{Raw}}\,{\rm{LAI}} = \frac{{{\rm{Intact}}\,{\rm{LTR}}\,{\rm{retrotransposon}}\,{\rm{length}}}}{{{\rm{Total}}\,{\rm{LTR}}\,{\rm{sequence}}\,{\rm{length}}}}\times 100\end{equation*}\end{document}
